// contentful.swift v0.9.2
import Contentful

// Retain the client as a property on a type you define so that 
// the client's asynchronous network callbacks are executed.
let client = Client(spaceId: "<space_id>", accessToken: "<access_token>")

let query = Query(whereLinkAtFieldNamed: "bestFriend",
                  forType: "cat",
                  hasValueAt: "fields.name",
                  ofType: "cat", that: .matches("Happy Cat"))

client.fetchEntries(with: query) { (result: Result<ArrayResponse<Entry>>) in
  switch result {
  case .success(let arrayResponse):
    let entries = arrayResponse.items
    print(entries)
  case .error(let error):
    print(error)
  }
}

